import React, { Component } from 'react';
import GeoComponentPush from './GeoPushComponent.js';
import MyText from './MyText.js';
import haverDistance from './myHaverSine.js';
import Component_1 from './pagedraw/component_1';
import Component_2 from './pagedraw/component_2';
import Component_3 from './pagedraw/component_3';
import Component_4 from './pagedraw/component_4';
import Component_5 from './pagedraw/component_5';

const myTargetLocation = { name: 'Runde tårn', description: ' Bygget af Christian 4 tal', coords: { latitude: 55.681438, longitude: 12.575719 } }; /// A sample target location

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      latitude: 0,
      longitude: 0,
      distance: 0,
      targetName: '',
      targetDescription: '',

      textInfo: 'rundetårn blev bygget i 1512 af Christian Holmblad'

    };
    this.e = null;
    this.updatePostionData = this.updatePostionData.bind(this);
    this.geoError = this.geoError.bind(this);
  }

  updatePostionData(position) {
    let theDistance = haverDistance(position.coords.latitude, myTargetLocation.coords.latitude, position.coords.longitude, myTargetLocation.coords.longitude);
    this.setState({ distance: theDistance, latitude: position.coords.latitude, longitude: position.coords.longitude, targetName: myTargetLocation.name, targetDescription: myTargetLocation.description });
  }

  geoError(err) {
    this.e = err.message;
  }


  render() {
    return (
      <div>
        <Component_1 textToDisplay={'Distance i meter til ' + this.state.targetName + ' : ' + Math.round(1000 * this.state.distance)} />
        <Component_2 textToDisplay={'Længdegrad på din nuværende position: ' + this.state.longitude} />
        <Component_3 textToDisplay={this.state.longitude} />
        <Component_4 textToDisplay={'Distance til ' + this.state.targetName + ' : ' + Math.round(1000 * this.state.distance) + ' m'} />
        <Component_5 textInfo={this.state.textInfo} />
        <GeoComponentPush GeoUpdatePositionData={this.updatePostionData} GeoError={this.geoError} />

        {/*         <MyText textToDisplay={'Breddegrad på din nuværende position:' + this.state.latitude} />
        <MyText textToDisplay={'Længdegrad på din nuværende position: ' + this.state.longitude} />
        <MyText textToDisplay={'Distance i meter til ' + this.state.targetName + ' : ' + Math.round(1000 * this.state.distance)} />
        <MyText textToDisplay={'Info om ' + this.state.targetName + ' :' + this.state.targetDescription} />
 */}
      </div>
    );
  }
}

export default App;
