import React, { Component } from 'react';
import haverDistance from './myHaverSine.js';
import Mainpush from './pagedraw/mainpush';
import Lokation from './Lokation';
import GeoPushComponent from './GeoPushComponent.js';


var lokation1 = new Lokation('Rundetårn',55.681346, 12.575795);          //HARDDODET DISTANCE
var lokation2 = new Lokation('Trianglen',55.699414, 12.577471);           //HARDCODET DISTANCE
var lokation3 = new Lokation('Svanemøllen st.', 55.715519, 12.577928);   //HARDCODET DISTANCE

var geoLokationer = new Array(Lokation);
geoLokationer = [lokation1, lokation2, lokation3];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      latitude:0,
      longitude:0
    };

    this.e = null;
    this.updatePostionData = this.updatePostionData.bind(this);
    this.geoError = this.geoError.bind(this);
  }

  //funktioner til Geo
  updatePostionData(position) {
     for (let i = 0 ; i < geoLokationer.length ; i++){  // her bruges antallet af lokationer
      let theDistance = haverDistance(position.coords.latitude, geoLokationer[i].latitude, position.coords.longitude, geoLokationer[i].longitude);
      geoLokationer[i].distanceTo = theDistance;
    }
    this.setState({longitude: position.coords.longitude, 
    latitude: position.coords.longitude})
  }

  geoError(err) {
    this.e = err.message;
  }

  findClosestLokation(lokationer){
    let smallestDistance = 200000000000000;
    for (let i = 0; i < lokationer.length; i++){

      if (lokationer[i].distanceTo < smallestDistance){
        smallestDistance = lokationer[i].distanceTo;
        var closestLocation = lokationer[i];
        console.log(lokationer[i].latitude)
      }
    }
    return closestLocation; 
  }

  render() {
    return (
      <div>
        <Mainpush textToDisplay={'Distance: '+ this.findClosestLokation(geoLokationer).distanceTo+' meter'}/>

       <GeoPushComponent GeoUpdatePositionData={this.updatePostionData} GeoError={this.geoError} />
      </div>
    );
  }
}


export default App;
