import React, { Component } from 'react';
import GeoComponentPush from './GeoPushComponent.js';
import MyText from './MyText.js';
import haverDistance from './myHaverSine.js';
import Mainpush from './pagedraw/mainpush';
import Lokation from './Lokation';


var lokation1 = new Lokation('rundetårn',60.000,80.66);

lokation1.description='lol';

const lokation2 =  { name: 'Rundetårn',
                      description: 'Bygget af Christian d. 4', 
                      coords: {latitude: 55.681438, longitude: 12.575719 } 
};

const lokation3 =  { name: 'Rundetårn',
                      description: 'Bygget af Christian d. 4', 
                      coords: {latitude: 55.681438, longitude: 12.575719 } 
};

const lokation4 =  { name: 'Rundetårn',
                      description: 'Bygget af Christian d. 4', 
                      coords: {latitude: 55.681438, longitude: 12.575719 } 
};

const lokation5 =  { name: 'Rundetårn',
                      description: 'Bygget af Christian d. 4', 
                      coords: {latitude: 55.681438, longitude: 12.575719 } 
};


class App extends Component {
  constructor(props) {
    super(props);
    this.state = {

    };

    this.e = null;
    this.updatePostionData = this.updatePostionData.bind(this);
    this.geoError = this.geoError.bind(this);
  }

  //funktioner til Geo

  updatePostionData(position) {
    for (let i=1;i<6;i++){  // her bruges antallet af lokationer +1 
      let theDistance = haverDistance(position.coords.latitude, 'lokation'+i.coords.latitude, position.coords.longitude, 'lokation'+i.coords.longitude);
      this.setState({ distance: theDistance, latitude: position.coords.latitude, longitude: position.coords.longitude, targetName: 'lokation'+i.name, targetDescription:'lokation'+i.description });
    }
  }

  geoError(err) {
    this.e = err.message;
  }

  findClosestLokationa(Array){
    let smallestDistance = infinity;

    for (i=0; i < array.length(); i++){
      if (array[i].distance < smallestDistance){
        array[i].distance = smallestDistance;
        closesLocation = array[i];
      }
    }
    return closesLocation; 
  }

  render() {
    return (
      <div>
        <Mainpush textToDisplay = {'Distance til Runde tårn: '+lokation1.description+'m'}/>
      </div>
    );
  }
}

export default App;
