import React, { Component } from 'react';
import haverDistance from './myHaverSine.js';
import Mainpush from './pagedraw/mainpush';
import Lokation from './Lokation';


var lokation1 = new Lokation('Rundetårn',55.681346, 12.575795, );
var lokation2 = new Lokation('Trianglen',55.699414, 12.577471, 2);
var lokation3 = new Lokation('Svanemøllen st.', 55.715519, 12.577928, 3);

var geoLokationer = new Array(Lokation);
var geoLokationer = [lokation1, lokation2, lokation3];

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {};

    console.log(geoLokationer[1].distanceTo);

    this.e = null;
    this.updatePostionData = this.updatePostionData.bind(this);
    this.geoError = this.geoError.bind(this);
  }
  //funktioner til Geo

  updatePostionData(position) {
    for (let i=1;i<6;i++){  // her bruges antallet af lokationer +1 
      let theDistance = haverDistance(position.coords.latitude, 'lokation'+i.coords.latitude, position.coords.longitude, 'lokation'+i.coords.longitude);
      this.setState({ distance: theDistance, latitude: position.coords.latitude, longitude: position.coords.longitude, targetName: 'lokation'+i.name, targetDescription:'lokation'+i.description });
    }
  }

  geoError(err) {
    this.e = err.message;
  }

  findClosestLokation(lokationer){
    let smallestDistance = 200;
    for (let i = 0; i < lokationer.length; i++){

      console.log(lokationer[i].lokationName)
      console.log(lokationer[i].longitude)
      console.log(lokationer[i].latitude)
      console.log(lokationer[i].distanceTo)

      if (lokationer[i].distanceTo < smallestDistance){
        smallestDistance = lokationer[i].distanceTo;
        var closestLocation = lokationer[i];
      }
    }
    return closestLocation; 
  }

  render() {
    return (
      <div>
        <Mainpush textToDisplay = {'Distance til: ' + this.findClosestLokation(geoLokationer)}/>
      </div>
    );
  }
}

export default App;
